  $(document).on('submit', '.form', function() {
    var form = $(this),
        error = 0;

    var rec = $('.g-recaptcha-response', form);    

    if(form.hasClass('js-personal-form') || form.hasClass('js-add-sale-form')) return true;

    form.find('.error').removeClass('error');

    //проверяем поля формы на пустоту
    form.find('.required').each(function() {
      if ($(this).val() == '' || ($(this).attr('type') == 'tel' && $(this).val().indexOf('_') > -1)
			  || ($(this).attr('type') == 'checkbox' && !$(this).is(':checked'))) {
        $(this).addClass('error');
        error = 1;
      }
    });

    form.find('.required-email').each(function() {
      var pattern = /^([a-z0-9_\.-])+@[a-z0-9-]+\.([a-z]{2,4}\.)?[a-z]{2,4}$/i;
      if ($(this).val() == '' || !pattern.test($(this).val())) {
        $(this).addClass('error');
        error = 1;
      }
    });

    if (error == 0 && !$(this).hasClass('js-not-ajax')) {
      // если ошибок нет то отправляем данные
      if(rec.length) {
            grecaptcha.ready(function() {
                grecaptcha.execute(window.reCAPTCHA_site_key, {action: 'submit'}).then(function(token) {
                    rec.val(token);
                    $.ajax({
                          url: form.attr('action'),
                          data: form.serialize(),
                          method: 'post',
                          success: function(result) {
                            form.replaceWith(result);
                          }
                    });
                });
            });    
      } 
      else {
            $.ajax({
                url: form.attr('action'),
                data: form.serialize(),
                method: 'post',
                success: function(result) {
                    form.replaceWith(result);
                }
            });
      }
    }

    if(error !=0 ||  !$(this).hasClass('js-not-ajax')) return false;
  });

  

