  function initMenu(menu) {
    let catalogMenu = new CatalogMenu(menu);
    catalogMenu.init();
    return catalogMenu;
  }

  function CatalogMenu(item) {
    this.el = item;
    this.activeRow = null;

    this.init = function() {
      let $menu = $(this.el);
      let self = this;

      // $menu.on('mouseleave', function() {
      //   if (!self.activeRow.dataset.submenu) {
      //     self.deactivateRow(self.activeRow);
      //     self.activeRow = null;
      //   }
      // });

      $menu.on('mouseenter', 'li', function() {
        if (!isMobile.any()) {
        if (self.activeRow) {
          self.deactivateRow(self.activeRow);
        }

        self.activateRow(this);
        self.activeRow = this;
        }
      });

//      $menu.on('click', 'li > a', function(e) {
//        let li = this.parentElement;
//
//        if (li.dataset.submenu && !this.classList.contains('active')) {
//          e.preventDefault();
//          this.classList.add('active');
//
//          if (self.activeRow) {
//            self.deactivateRow(self.activeRow);
//        }
//  
//          self.activateRow(li);
//          self.activeRow = li;
//        }
//      });

	  if (is_touch_device()) {

		$menu.on('click', 'li > a', function(e) {
		  let li = this.parentElement;

		  if (li.dataset.submenu && !this.classList.contains('active')) {
			e.preventDefault();
			this.classList.add('active');

			if (self.activeRow) {
			  self.deactivateRow(self.activeRow);
		  }

			self.activateRow(li);
			self.activeRow = li;
		  }
		});

	  }
	  
    };
  }

  CatalogMenu.prototype.hasActiveRow = function() {
    return !!this.activeRow;
  };

  CatalogMenu.prototype.activateRow = function(row) {
    let $newRow = $(row);
    $newRow.addClass('active');

    if (isMobile.any() && window.innerWidth < 768) {
      let $forhide = $newRow.closest('.js-menu-section');
	  if($forhide.length) {
		if($forhide.data('menu')) {
			menuSections.filter(`[data-menu=${$forhide.data('menu')}]`).hide();
		}
		else {
			$forhide.hide();
		}
	  }
      //$newRow.closest('.js-menu-section').hide();
    }
    menuSections.filter(`[data-menu=${$newRow.data('submenu')}]`).delay(100).show();
  };

  CatalogMenu.prototype.deactivateRow = function(row) {
    if (row.dataset.submenu) {
      this.closeAllSubMenu(row);
    }

    if (isMobile.any() && window.innerWidth < 768) {
      let $forshow = $(this.el).parent();
	  if($forshow.length) {
			if($forshow.data('menu')) {
				menuSections.filter(`[data-menu=${$forshow.data('menu')}]`).show();
			}
			else {
				$forshow.show();
			}
	  }
	  //$(this.el).parent().show();
    }

    $(row).removeClass('active').find('a').removeClass('active');
  };

  CatalogMenu.prototype.closeAllSubMenu = function closeAllSubMenu(row) {
    let section = menuSections.filter(`[data-menu=${$(row).data('submenu')}]`);
    let subLiActive = section.find('li.active');

    if (subLiActive.length && subLiActive.data('submenu')) {
      closeAllSubMenu(subLiActive[0]);
    }

    section.hide().find('li.active').removeClass('active');
  };

