  $('[data-countwin]').each(function() {
	  let item = $(this);

	  function countUpdate() {

		  var item_date = parseInt(item.data('countwin'));
		  var now = new Date().getTime();
		  if(item_date > now) {
			let count = countdown(now, item_date, [countdown.DAYS | countdown.HOURS | countdown.MINUTES | countdown.SECONDS]);

			let days = count.days;

			if (days == 0) { days = ''; }
			else { days = (count.days<10) ? '0'+count.days+'д' : count.days+'д'; }

			let hours = (count.hours<10) ? '0'+count.hours : count.hours;
			let minutes = (count.minutes<10) ? '0'+count.minutes : count.minutes;
			let seconds = (count.seconds<10) ? '0'+count.seconds : count.seconds;

			item.text( days + ' ' + hours + ':' + minutes + ':' + seconds );
		  }
		  else {
			item.text( 'закрыт' );
		  }
		  if(item_date <= now + 3600000) {
			item.blink({
			  'css': {
				  'color': '#ff0000'
			  },
			  'duration': 200,
			  'delay_in': 200,
			  'delay_out': 200,
			  'count': 1
			});
		  }
		  requestAnimationFrame(countUpdate);

	  }
	  countUpdate();
  });
  
