	function logslider(position, reverse, minn, maxn) { 
	  // position will be between 0 and 100
	  var minp = 0;
	  var maxp = 100;

	  // The result should be between 100 an 10000000
	  if(minn == 0) minn = 0.01;
	  var minv = Math.log(minn);
	  var maxv = Math.log(maxn);

	  // calculate adjustment factor
	  var scale = (maxv - minv) / (maxp - minp);

	  return reverse
		? (Math.log(position) - minv) / scale + minp
		: Math.exp(minv + scale * (position - minp))
		;
	};
	
	$(".js-range-slider").each(function(){
	  var minn = $(this).data('minp');
	  var maxn = $(this).data('maxp');
	  var round = $(this).data('round');
	  $(this).ionRangeSlider({
		  skin: "square",
		  hide_min_max: true,
		  from: logslider($(this).parent().find('.js-val-min').val(), true, minn, maxn),
		  to: logslider($(this).parent().find('.js-val-max').val(), true, minn, maxn),
		  hide_from_to: true,
		  onChange: function(data) {
			var min = $(data.input).parent().find('.js-val-min');
			var max = $(data.input).parent().find('.js-val-max');
//			console.log(data);
			min.val(data.from_pretty);
			max.val(data.to_pretty);
		  },
		  onFinish: function(data) {
			var min = $(data.input).parent().find('.js-val-min');
			var max = $(data.input).parent().find('.js-val-max');
			min.val(data.from_pretty);
			max.val(data.to_pretty);
			get_content_from_slider(min, max);
		  },
		  prettify(position) {
			if(round) 
			  return Math.round(logslider(position, false, minn, maxn));
			else 
			  return logslider(position, false, minn, maxn).toFixed(2);
		  }
	  });
	  
	});

